package uz.revolution.icecreammagnit.appimport android.app.Applicationimport uz.revolution.icecreammagnit.database.AppDatabaseimport uz.revolution.icecreammagnit.models.Supplierclass App : Application() {    override fun onCreate() {        super.onCreate()        AppDatabase.initDatabase(this)        val database = AppDatabase.get.getDatabase()        val getMagnitDao = database.getProductDao()        if (getMagnitDao?.getAllSuppliers()?.size == 0) {            getMagnitDao.insertSupplier(Supplier("Royal Muz Servis"))            getMagnitDao.insertSupplier(Supplier("Vazira MChJ"))            getMagnitDao.insertSupplier(Supplier("Imkon Plus"))            getMagnitDao.insertSupplier(Supplier("Ice & Gold(Bekobod)"))            getMagnitDao.insertSupplier(Supplier("Dairy Classic(singapur)"))            getMagnitDao.insertSupplier(Supplier("Ulug'bek Aka(Buxoro)"))            getMagnitDao.insertSupplier(Supplier("Boshqalar..."))        }    }}