package uz.revolution.icecreammagnit.appimport android.app.Applicationimport uz.revolution.icecreammagnit.database.AppDatabaseimport uz.revolution.icecreammagnit.models.Supplierclass App : Application() {    override fun onCreate() {        super.onCreate()        AppDatabase.initDatabase(this)        val database = AppDatabase.get.getDatabase()        val getMagnitDao = database.getProductDao()        if (getMagnitDao?.getAllSuppliers()?.size == 0) {            getMagnitDao.insertSupplier(Supplier(0,"Royal Muz Servis"))            getMagnitDao.insertSupplier(Supplier(1,"Vazira MChJ"))            getMagnitDao.insertSupplier(Supplier(2,"Imkon Plus"))            getMagnitDao.insertSupplier(Supplier(3,"Ice & Gold(Bekobod)"))            getMagnitDao.insertSupplier(Supplier(4,"Dairy Classic(singapur)"))            getMagnitDao.insertSupplier(Supplier(5,"Ulug'bek Aka(Buxoro)"))            getMagnitDao.insertSupplier(Supplier(6,"Boshqalar..."))        }    }}