package uz.revolution.icecreammagnit.modelsimport androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.Ignoreimport androidx.room.PrimaryKeyimport java.io.Serializable@Entity(tableName = "magnit")class Magnit : Serializable {    @PrimaryKey(autoGenerate = true)    @ColumnInfo(name = "id")    var id: Int = 0    @ColumnInfo(name = "date")    var date: String? = null    @ColumnInfo(name = "product")    var product: String? = null    @ColumnInfo(name = "total_box")    var totalBox: Int = 0    constructor()    @Ignore    constructor(date: String, product: String, totalBox: Int) {        this.date = date        this.product = product        this.totalBox = totalBox    }}